# Kode ini diambil dari https://github.com/yyc9268/Numerical_optimization/blob/49639aa66b0f1dd47803110ae47b1083e4217b51/matlab/multivariate_smooth/find_step_length.m# Kode telah dimodifikasi sesuai dengan kebutuhan soal dan agar lebih generic# Copyright (C) 2017 Young-Chul Yoon# All rights reserved.function [alpha]=generic_line_search(f, X, p, max_iter, mode)  iter = 0;  alpha = 10;  c = 0.5;  rho = 0.5;  n = length(X);  left = zeros(n, 1);    if mode == 0  # Find alpha intuitively  while iter < max_iter      left = feval(f, X+(p*alpha));      right = feval(f, X);      if left < right          step_length = alpha;          return;      else          alpha = alpha*rho;      end      iter += 1;  end  else      end