function [X]=quasi_newton_method(f, X, max_iter, tol)  iter = 0;  n = length(X);  B = eye(n);  while iter < max_iter    g = find_grad(f, X);    p = -B*g        # Begin line search    alpha = generic_line_search(f, X, p, 100, 0);    # iterasi x    X_new = X + alpha*p;        #hitung SR-1    g_new = find_grad(f, X_new);    y_k = g_new - g;    d_k = X_new - X;    B_old = B;    div = (d_k-(B_old*y_k))'*y_k    B = B_old + ((d_k-(B_old*y_k)) + (d_k-(B_old*y_k))')/div    iter += 1  end end